item.value:
// Treat log type
switch(nextLog.getType())
	{
	/*
	 * ---------------------------------------------------
	 * done : Affichage du prochain log sur l'UI / console
	 *
	 * Remarque : -
	 * ---------------------------------------------------
	 */
	case WAITING:
		/*
		 * ---------------------------------------------------------------------------------
		 * done : Gestion d'un log indiquant l'attente d'un thread sur sa ressource partagee
		 *
		 * Remarque : cette etape consiste a gerer vos lites d'attentes
		 * ---------------------------------------------------------------------------------
		 */
		System.out.println("\tThread (" + nextLog.getPerson().getName() + ") is waiting to access the document");

		break;
	case PROCESSING:
		/*
		 * -----------------------------------------------------------------------------
		 * done : Gestion d'un log indiquant l'acces d'un thread a sa ressource partagee
		 *
		 * Remarque : cette etape consiste a gerer vos lites d'attentes
		 * -----------------------------------------------------------------------------
		 */
		System.out.println("\tThread (" + nextLog.getPerson().getName() + ") is processing the document");

		break;
	case FINISHED:
		/*
		 * -----------------------------------------------------------------------------------------------
		 * done : Gestion d'un log indiquant la fin d'un thread et la liberation de l'acces a sa ressource
		 *
		 * Remarque : cette etape consiste a gerer vos lites d'attentes
		 * -----------------------------------------------------------------------------------------------
		 */
		System.out.println("\tThread (" + nextLog.getPerson().getName() + ") has finished to read the document");

		break;
	default:
		System.out.println("\tThread (" + nextLog.getPerson().getName() + ") is doing non conventionnal actions");

		break;
	}
